import React, {useEffect, useState} from 'react'
import {Table } from 'react-bootstrap'
import axios from 'axios'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {faStar as faStarSolid } from '@fortawesome/free-regular-svg-icons'
import { faStar as faStarRegular } from '@fortawesome/free-regular-svg-icons'
{/*import './table.css'
import { useTable } from 'react-table'
 import MOCK_DATA from '../MOCK_DATA.json'
 import { COLUMNS } from './columns'
 import './table.css' */ }

function RepoTable() {
    const [repos, setRepos] = useState([]);

    useEffect(() => {
        axios
            .get("https://api.github.com/search/repositories?q=created:%3E2019-01-10&sort=stars")
            .then((res) => {
                console.log(res);
                setRepos(res.data.items)
            })
            .catch(err => {
                console.log(err)
            });

    }, []);
    console.log(repos);

    return (
    <div>
     <Table striped bordered hover variant ="light">
        <thead>
            <tr>
                <th>Full name and name of repository</th>
                <th>Git Link</th>
                <th>Description</th>
                <th>Favorites</th>
            </tr>
        </thead>
        <tbody>{repos.map((repo) => 
                    <tr key ={repo.id}>
                        <td>{repo.full_name}</td>
                        <td>{repo.url}</td>
                        <td>{repo.description}</td>
                        <td>{repo.stargazer_count}</td> 
                        <td> <FontAwesomeIcon icon={"faStar"} />

                        </td>

                    </tr>
                        )
                        }


        </tbody>
    </Table>
    </div>

    );
}

export default RepoTable
