{/*import React, { useEffect, useState } from 'react'*/}
import { Table } from 'react-bootstrap'
{/*import axios from 'axios'*/}
import { AiFillStar } from 'react-icons/ai'
import { AiOutlineStar } from 'react-icons/ai'
{/*import './table.css'
import { useTable } from 'react-table'
 import MOCK_DATA from '../MOCK_DATA.json'
 import { COLUMNS } from './columns'
 import './table.css' */ }

export default class RepoTable extends Component {
    constructor(props) {
        super(props);
        this.state = {
            isStarred: false
        };
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick() {
        this.setState({
            isStarred: !this.state.liked
        });
    }
    render() {
        {/*const [repos, setRepos] = useState([]);

            useEffect(() => {
                axios
                    .get("https://api.github.com/search/repositories?q=created:%3E2019-01-10&sort=stars")
                    .then((res) => {
                        console.log(res);
                        setRepos(res.data.items)
                    })
                    .catch(err => {
                        console.log(err)
                    });

            }, [])*/}

        return(

           <div>
            <Table striped bordered hover variant="light">
                <thead>
                    <tr>
                        <th>Rating</th>
                        <th>Full name and name of repository</th>
                        <th>Git Link</th>
                        <th>Description</th>
                        <th>Favorites</th>
                    </tr>
                </thead>
                <tbody>
                    {repos.map((repo) =>
                        <tr key={repo.id}>
                            <td>{repo.stargazers_count}</td>
                            <td>{repo.full_name}</td>
                            <td>{repo.url}</td>
                            <td>{repo.description}</td>
                            <td>
                                <button onClick={(this.HandleClick)}>
                                    {this.state.isStarred ? <AiFillStar></AiFillStar> : <AiOutlineStar></AiOutlineStar>}
                                </button>
                            </td>
                        </tr>
                    )}
                </tbody>
            </Table>
            </div>
        );



    }}
